<?php/** * Enquete *   * @author Andre * @version 1.0 */class Application_Model_Enquete extends Zend_Db_Table_Abstract {		protected $_name = 'enquete';	protected $_primary = 'id_enquete';		/**	 * Metodo que salva um registro, sendo adaptado para inserir ou fazer update	 * @param $arrDataForm	 * @return $boolean || $newInsertId	 */	public function save( $arrDataForm ) {		if ( $arrDataForm['id_enquete'] != 0 || $arrDataForm['id_enquete'] != '' ) {						return $this->update( $arrDataForm , " id_enquete = '" . $arrDataForm['id_enquete'] ."'");		} else { // estou incluindo						unset( $arrDataForm['id_enquete'] );			return $this->insert( $arrDataForm );		}	}		public function listar(){				/* Estabelece conexao com o banco */		$db = Zend_Registry::get('db');					$select = $db->select()->from( array( "e" => "enquete" ), new Zend_Db_Expr ( "id_enquete, no_titulo" ) )								->order("id_enquete DESC")								->limit("1"); 		$select = $db->query($select);		        return $select->fetchAll();	}		public function listarId( $id ){				/* Estabelece conexao com o banco */		$db = Zend_Registry::get('db');					$select = $db->select()->from( array( "e" => "enquete" ), new Zend_Db_Expr ( "id_enquete, no_titulo" ) )								->where("id_enquete = " . $id)								->limit("1"); 		$select = $db->query($select);		        return $select->fetchAll();	}}