<?php/** * Pergunta *   * @author Andre * @version 1.0 */class Application_Model_Pergunta extends Zend_Db_Table_Abstract {		protected $_name = 'pergunta';	protected $_primary = 'id_pergunta';		/**	 * Metodo que salva um registro, sendo adaptado para inserir ou fazer update	 * @param $arrDataForm	 * @return $boolean || $newInsertId	 */	public function save( $arrDataForm ) {		if ( $arrDataForm['id_pergunta'] != 0 || $arrDataForm['id_pergunta'] != '' ) {						return $this->update( $arrDataForm , " id_pergunta = '" . $arrDataForm['id_pergunta'] ."'");		} else { // estou incluindo						unset( $arrDataForm['id_pergunta'] );			return $this->insert( $arrDataForm );		}	}}