<?php/** * Resposta *   * @author Andre * @version 1.0 */class Application_Model_Resposta extends Zend_Db_Table_Abstract {		protected $_name = 'resposta';	protected $_primary = 'id_resposta';		/**	 * Metodo que salva um registro, sendo adaptado para inserir ou fazer update	 * @param $arrDataForm	 * @return $boolean || $newInsertId	 */	public function save( $arrDataForm ) {		if ( $arrDataForm['id_resposta'] != 0 || $arrDataForm['id_resposta'] != '' ) {						return $this->update( $arrDataForm , " id_resposta = '" . $arrDataForm['id_resposta'] ."'");		} else { // estou incluindo						unset( $arrDataForm['id_resposta'] );			return $this->insert( $arrDataForm );		}	}}