<?php/** * Resultado *   * @author Andre * @version 1.0 */class Application_Model_Resultado extends Zend_Db_Table_Abstract {		protected $_name = 'resultado';	protected $_primary = 'id_resultado';		/**	 * Metodo que salva um registro, sendo adaptado para inserir ou fazer update	 * @param $arrDataForm	 * @return $boolean || $newInsertId	 */	public function save( $arrDataForm ) {		if ( $arrDataForm['id_resultado'] != 0 || $arrDataForm['id_resultado'] != '' ) {						return $this->update( $arrDataForm , " id_resultado = '" . $arrDataForm['id_resultado'] ."'");		} else { // estou incluindo						unset( $arrDataForm['id_resultado'] );			return $this->insert( $arrDataForm );		}	}}